import { configureStore,createSlice } from "@reduxjs/toolkit";



const cartSlice = createSlice({
    name:'cart',
    initialState: {
        items : []
    },
    reducers:{
        addItem(state,action){
            const newItem = action.payload
            console.log()
            const existingItem = state.items.find(item=>item.id===newItem.id)
            if (!existingItem){
                state.items.push({
                    itemId: newItem.id,
                    price: newItem.price,
                    quantity:newItem.quantity,
                    totalPrice: newItem.price* newItem.quantity,
                    name: newItem.title
                })
            }else{
                existingItem.quantity+=1
                existingItem.totalPrice+= existingItem.price
            }
        },
        removeItem(state,action){
            const id = action.payload
            const neededItemIndex = state.items.findIndex(item=>item.id===id)
            if (state.items[neededItemIndex].quantity===1){
                state.items = state.items.filter(item=>item.id!==id)
            }else{
                state.items[neededItemIndex].quantity-=1
                state.items[neededItemIndex].totalPrice -= state.items[neededItemIndex].price
            }
        }
    }
})

const store = configureStore({
    reducer: cartSlice.reducer
})

export const cartActions = cartSlice.actions

export default store
